CREATE TABLE IF NOT EXISTS `dou_article` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `cat_id` smallint(5) NOT NULL DEFAULT '0',
  `title` varchar(150) NOT NULL DEFAULT '',
  `defined` text,
  `content` longtext,
  `image` varchar(255) DEFAULT NULL,
  `keywords` varchar(255) DEFAULT NULL,
  `add_time` int(10) unsigned NOT NULL DEFAULT '0',
  `click` smallint(6) unsigned NOT NULL DEFAULT '0',
  `description` varchar(255) NOT NULL DEFAULT '',
  `sort` tinyint(1) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=7 ;

--
-- 转存表中的数据 `dou_article`
--

INSERT INTO `dou_article` (`id`, `cat_id`, `title`, `defined`, `content`, `image`, `keywords`, `add_time`, `click`, `description`, `sort`) VALUES
(1, 1, '移动互联网产品设计的核心要素有哪些？', '', '移动互联网和传统互联网的设计有很多不同&lt;br /&gt;\r\n\r\n移动互联网和传统互联网的设计有很多不同，针对前者有哪些关键的设计重点、考虑要素、交互或体验要特别注意的呢？本文来自知乎网友可风的回答。&lt;br /&gt;\r\n&lt;br /&gt;\r\n可风&lt;br /&gt;\r\n&lt;br /&gt;\r\n最近越来越多的圈内人开始随大潮进入移动互联网领域，从传统的web或者桌面端设计开始学习移动互联网产品的设计。在很多人眼里，设计移动互联网产品和传\r\n统互联网产品的区别无非就是载体从电脑变成了手机，所以只要熟悉一下各个手机中不同的规范和特性就算是完成了过渡，学习了下ios human \r\nguideline，了解了一下拟物化设计和扁平化设计，就以为是了解了移动互联网的设计方法。其实这种思想完全是只看到表现而没看到本质的错误，移动互\r\n联网和传统互联网的区别不光是设计标准和设计规范的变化，而应该从整个物理环境的变化来重新全面的认识。那么我们分析一下，移动互联网产品的用户体验和传\r\n统互联网产品有什么区别呢？&lt;br /&gt;\r\n&lt;br /&gt;\r\n一、使用场景的复杂&lt;br /&gt;\r\n&lt;br /&gt;\r\n用户在使用桌面客户端或者访问web页面的时候，多半是坐在电脑前，固定的盯着屏幕和使用键鼠操作，这个时候对于用户来说，使用场景是简单而固定的。但是\r\n在使用手机的时候，用户可能在地铁中，在公交上，在电梯中，无聊等待时或者边走路边用。如此复杂的场景，需要产品的设计者考虑的要素也自然非常的复杂。&lt;br /&gt;\r\n&lt;br /&gt;\r\n比如在公交车上拥挤和摇晃时，用户如果才能顺畅的单手操作？比如在地铁或者电梯的信号不好的情况下，是否要考虑各种网络情况带来的问题？比如用户在无聊等待玩游戏，或者在床上睡前时，又变成了深入沉浸的体验？不同的场景不同的情况在设计时是否都有考虑清楚？&lt;br /&gt;\r\n&lt;br /&gt;\r\n二、使用时间碎片化&lt;br /&gt;\r\n&lt;br /&gt;\r\n用户在使用电脑时，大部分时间还是固定的，无非可能因为工作和同事沟通一下，或者起身上个厕所，一般都有10-20分钟完整的时间片在操作电脑。但是移动\r\n端就不一样了，用户既然在移动，使用手机时要随时随地观察周围的情况，随时可能中断现在的操作，锁屏，再继续刚才的操作。所以用户使用移动产品的时间不是\r\n连成片的，而是一段一段的，有的时候中断回再回来，有的时候中断就不会回来了。&lt;br /&gt;\r\n&lt;br /&gt;\r\n三、屏幕尺寸的缩小&lt;br /&gt;\r\n&lt;br /&gt;\r\n用户使用电脑产品的屏幕尺寸是可以很大的，小则13寸大到27寸，这样使得桌面产品和web产品有充足的区域展现信息，整个界面利用率就可以很高。我们在\r\n做交互设计的时候会有一种方法，如果一个次要信息的出现不会影响大部分用户的时候，那么这个次要信息是可以放在界面上的，这就是为什么网站可以加入很多广\r\n告banner的原因，因为只要保持到一个度不影响正常的使用就不会破坏整体的用户体验。但是在移动端，这个度是非常的小的，因为屏幕尺寸的限制，本身需\r\n要你展现的必要信息都需要有一个合理的规划和抉择，次要的信息再一出来肯定破坏体验。将前2条结合你会发现，用户在使用移动产品是需要非常追求效率的，所\r\n以移动端产品的设计难道会大大增加。&lt;br /&gt;\r\n&lt;br /&gt;\r\n四、无法多任务的处理信息&lt;br /&gt;\r\n&lt;br /&gt;\r\n用户在使用桌面产品时，是更加容易的进行多任务操作和处理的，比如我正在浏览web查资料，又正在进行文档的整理，还可能开着QQ和朋友聊天。因为大屏幕\r\n的关系和系统机制让用户能够高效的同时处理多个信息，当然，还得益于固定的使用场景和完整的时间片。但是因为前面也提到的问题，移动端的产品往往是沉浸式\r\n的，用户在同一时期只可能使用一个应用，完成一个流程，然后结束，再去开启另一个应用和另一个流程，所以大部分移动产品设计时往往讲求遵循的是单一的任务\r\n流，期间结束和跳转的设计非常的少。&lt;br /&gt;\r\n&lt;br /&gt;\r\n五、平台的设计规范和特性&lt;br /&gt;\r\n&lt;br /&gt;\r\n最后才是各自的平台规范和标准，比如什么ios \r\nhuman \r\nguideline或者WindowsPhone的metro理念，纵观知乎和各大网站，很多人每天关注的都是这些比如拟物化设计和扁平化设计的风格，返\r\n回按钮的逻辑或者隐藏title之类的方法论细节。的确你了解这些信息是可以快速方便的设计出一个可用的移动产品的，但是如果没有了解之前所说的几条移动\r\n产品和传统互联网产品在用户体验上的区别，你可能永远也无法参透移动互联网用户体验的规律和本质。&lt;br /&gt;', './images/article/56f7ce180709d.jpg', '', 1459080788, 4, '', 1),
(2, 1, 'IT外包服务解决方案 ', '', '&lt;p style=&quot;text-align:left;background:#f9f9f9;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;IT外包服务解决方案&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;一、什么是IT外包？&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;随着经济的高速发展，信息时代的到来，计算机已成为企业不可缺少的工具。而且随着信息技术的发展，企业会面临越来越多的计算机及网络系统的维护、管理、规划等问题。如计算机系统硬件故障、病毒防范及系统升级，特别是对非专业IT维护的企事业单位，如果不能及时有效高效地处理好这些事关企业IT故障和问题，不但给企业的商务信息平台的运行带来影响，更是对企业的核心数据安全带来隐患。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;而我们提供的全面的、高效的、专业的企业IT外包服务，能使企业在遇到以上问题时能迎刃而解。同时也让中小企业以低成本换来较为专业的IT外包服务，以最高的性价比保证企业计算机、网络、数据库等电子商务系统的良好运维，以解除您的后顾之忧，使企业专注于发展自己的核心业务，为您能在自己的专业领域不断前行而保驾护航。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;二、服务内容&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;table style=&quot;width:416.1pt;border-collapse:collapse;&quot; class=&quot;MsoNormalTable&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;center&quot; rowspan=&quot;2&quot; width=&quot;110&quot;&gt;\r\n				&lt;p style=&quot;text-align:center;&quot; class=&quot;MsoNormal&quot; align=&quot;center&quot;&gt;\r\n					&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;电脑维护&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;center&quot; width=&quot;443&quot;&gt;\r\n				&lt;p style=&quot;text-align:left;&quot; class=&quot;MsoNormal&quot;&gt;\r\n					&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;◇ 台式机、笔记本及服务器的硬件维护&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp; 对于客户日常使用的各种办公设备进行硬件故障处理，定期保养； &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;center&quot; width=&quot;443&quot;&gt;\r\n				&lt;p style=&quot;text-align:left;&quot; class=&quot;MsoNormal&quot;&gt;\r\n					&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;◇ 外接设备维护&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 对于客户所使用的各种外接设备，例如打印机、扫描仪、传真机等实行维护及故障处理； &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;center&quot; width=&quot;110&quot;&gt;\r\n				&lt;p style=&quot;text-align:center;&quot; class=&quot;MsoNormal&quot; align=&quot;center&quot;&gt;\r\n					&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;系统维护&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;center&quot; width=&quot;443&quot;&gt;\r\n				&lt;p style=&quot;text-align:left;&quot; class=&quot;MsoNormal&quot;&gt;\r\n					&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;◇ 操作系统及办公软件的维护&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt; &amp;nbsp;&amp;nbsp; 处理客户由于使用不当或是病毒所造成的系统瘫痪、蓝屏、无法开机等操作系统的各种常见问题，并对操作系统及应用软件等进行各种优化及故障处理； &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;center&quot; rowspan=&quot;2&quot; width=&quot;110&quot;&gt;\r\n				&lt;p style=&quot;text-align:center;&quot; class=&quot;MsoNormal&quot; align=&quot;center&quot;&gt;\r\n					&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;网络维护&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;center&quot; width=&quot;443&quot;&gt;\r\n				&lt;p style=&quot;text-align:left;&quot; class=&quot;MsoNormal&quot;&gt;\r\n					&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;◇ 局域网及互联网维护&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;向企业提供网络线路布线方案，网络维护及网络优化方案。保证企业内部及外网的正常运作；另外我司免费提供企业网络技术培训服务及网络日常维护等课程； &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;center&quot; width=&quot;443&quot;&gt;\r\n				&lt;p style=&quot;text-align:left;&quot; class=&quot;MsoNormal&quot;&gt;\r\n					&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;◇ 局域网设备维护&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;对于客户所使用的各种内部局域网设备，如路由器、交换机、集线器等设备进行定期保养及排除故障； &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;center&quot; rowspan=&quot;3&quot; width=&quot;110&quot;&gt;\r\n				&lt;p style=&quot;text-align:center;&quot; class=&quot;MsoNormal&quot; align=&quot;center&quot;&gt;\r\n					&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;安全服务&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td valign=&quot;center&quot; width=&quot;443&quot;&gt;\r\n				&lt;p style=&quot;text-align:left;&quot; class=&quot;MsoNormal&quot;&gt;\r\n					&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;◇ 病毒防治&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt; &amp;nbsp; 由于目前的计算机病毒种类越来越多，而且破坏力也在不断增强， 我们为用户提供整套的防病毒方案，使用多种防毒软件， 如瑞星、金山毒霸、 Norton、PC-Cillin、 KV-3000等， 完全杜绝病毒对计算机造成破坏的可能性； &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;center&quot; width=&quot;443&quot;&gt;\r\n				&lt;p style=&quot;text-align:left;&quot; class=&quot;MsoNormal&quot;&gt;\r\n					&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;◇ 重要文件定期备份&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp; 对于客户电脑内比较重要的资料， 如：合同文档、资料等，我们提供多种备份方式，例如使用 移动硬盘、磁带机、CD-R、备份服务器。使备份数据完全与本机分离，实现真正意义的异地备份功能，真正保证了数据资料的绝对安全性； &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td valign=&quot;center&quot; width=&quot;443&quot;&gt;\r\n				&lt;p style=&quot;text-align:left;&quot; class=&quot;MsoNormal&quot;&gt;\r\n					&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;◇ 操作系统备份&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt; &amp;nbsp;&amp;nbsp;&amp;nbsp;运用多种方式，将办公用机的操作系统以及基本系统数据进行安全备份，例如Ghost备份、影子系统等，即使电脑出现故障，也能将电脑内的各种数据完好的恢复。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;\r\n&lt;p style=&quot;text-align:left;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;三、IT外包签约流程&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span&gt;&lt;img src=&quot;file://C:/Users/ADMINI~1/AppData/Local/Temp/ksohtml/wpsDF67.tmp.jpg&quot; width=&quot;386&quot; height=&quot;265&quot; /&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;四、合作方式&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;1、应急响应服务&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;与客户签约IT外包合同，我司将对客户的电脑、网络做初始化工作，例如设备资产登记，电脑全面检查，软硬件的标准化等。前三个月签订试用合同，由客户考察我司服务质量及技术，如客户对我司服务满意则签订正式合同，期限为一年。 &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;1.1 技术人员定期上门服务，进行系统稳定性、安全性的优化维护工作，降低系统故障率，并随时解决问题。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;1.2 紧急故障现场处理，发生紧急问题后迅速赶赴现场，为用户处理问题。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2、常驻外派服务&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;我司派遣一位IT工程师常驻客户现场服务，一年以上的工作经验。对于IT工程师的资质，客户如有特殊需求，可直接向我司提出，我司会提供客户的实际情况提供最佳人选。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;2.1 服务时间：法定工作日，作息时间由客户安排，客户也可选择每周固定天数现场服务，如每周一常驻客户现场服务。&lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;2.2 适用范围：公司网络环境规模中大型，台数50台以上。 &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;2.3 我们提供的不仅仅是合格的技术人才，我们提供的是强大的技术团队。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;', './images/article/5756c02d0cda8.jpeg', '', 1465303085, 3, '我司派遣一位IT工程师常驻客户现场服务，一年以上的工作经验。对于IT工程师的资质，客户如有特殊需求，可直接向我司提出，我司会提供客户的实际情况提供最佳人选。', 0),
(3, 0, 'IT外包服务解决方案 ', '', '&lt;p style=&quot;text-align:left;background:#f9f9f9;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;企业ERP及办公OA系统现状分析&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;目前大多企业在ERP及办公OA系统中采用了大量的PC作为工作用机。采用PC的优势在于灵活，本地处理能力非常强大。然而这些优势给我们带来了很大的难题，主要表现为以下几点：&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21.1pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;1、数据安全存在隐患&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;用户可以通过使用移动硬盘等设备任意拷贝数据，造成重要信息外泄，从而给公司带来重大的损失。另外，由于PC本地有硬盘，易受病毒感染，造成重要数据被破坏，也可能给企业带来不可估量的损失。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21.1pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2、维护量大&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;故障率较高。PC配备齐全，经过一段时间的使用后，硬件故障和软件故障逐渐增多，系统维护工作量大，在计算机维护人员相对紧张的情况下，系统管理将随着PC增加而带来更大压力。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;当前基于Windows的病毒无孔不入，在大量使用PC的环境中必须确保每台PC都有更好的防护措施，避免因个别PC感染病毒而影响整个网络，这使管理及维护难度大大提高。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21.1pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;3、系统损坏数据丢失&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;在当前能源短缺严重的情况下，经常出现断电现象，由于在企业中通常没有给PC配备UPS，导致系统易损坏、数据易丢失，从而给我们带来一定的损坏。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21.1pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;4、设备投入量较大&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;PC经常升级换代，基本上PC机使用3年以上就接近报废，无法满足正常的工作需要，需要重新投入购置新机，所以设备投入量较大。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;推荐方案&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:center;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot; align=&quot;center&quot;&gt;\r\n	&lt;b&gt;&lt;span&gt;&lt;img src=&quot;file://C:/Users/ADMINI~1/AppData/Local/Temp/ksohtml/wps8A47.tmp.png&quot; width=&quot;500&quot; height=&quot;339&quot; /&gt;&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;方案说明：&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;1、在本方案中，所有企业员工在工作过程中需要用到的软件（如Office、企业ERP系统等）均统一安装在终端服务器上，只需实现一次安装即可。后续的升级也只需在终端服务器上实现一次升级即可。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;2、企业的信息化系统中，终端主要应用在办公OA、ERP、生产调度等系统中，目前这些系统有基于B/S架构开发的，也有前端基于C/S架构开发的，建议用户采用云终端，使用RDP连接Window2003服务器运行前端基于B/S架构开发的以及前端基于C/S架构开发的系统及OA软件。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;3、在信息系统中，云终端通过RDP与终端服务器的连接时需要的带宽约20K/byte，目前企业内部的网络的带宽基本都已达到100M，均能满足要求，无需进行任何改造。在应用过程中，若客户端数量较多，通常会采用多台服务器来提供终端服务，为了提高终端模式的可靠性和灵活的可扩展性，建议对终端服务器组成网络负载平衡集群。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;4、目前较多的企业在全国各地都有分支机构，这些分支机构与总公司通常没有专线相连，较多的是在Internet上建立VPN连接，对于这样的VPN连接，网络稳定性不能保证，建议在分支机构布置终端服务器，为当地的云终端提供服务。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;应用优势：&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;整体投入低&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;在进行信息系统建议时，云终端方案较PC模式在成本上低10%-20%，但在PC模式中由于其后续的维护成本及升级成本较高，据统计，若使用云终端作为客户端，其5年总TCO比PC模式最多可低57％。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;提高数据了安全性&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;管理员能控制用户的数据输入输出，完全能防止用户使用移动硬盘等设备拷贝信息数据的可能。另外Windows系统最怕非法关机，这样可能损伤硬盘，丢失数据，甚至导致系统崩溃。而采用云终端方案，系统开机后直接进入应用程序，无论何时关机都不会造成系统和数据的损坏，再次开机后仍能回到关机前的状态。完全可以避免由于断电或误操作而引起系统崩溃等一系列问题。同时云终端本地没有硬盘、光驱、软驱等设备，数据没有带出的出口，病毒也没有入口，可以很好的实现数据安全以及有效防范病毒。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;集中管理&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;在PC模式中，客户端的应用软件和IP地址可任意改动，管理极为困难。而采用云终端方案，可以做到真正的集中管理，全部的数据和应用程序都放在终端服务器上，应用程序的安装和版本升级只需要在服务器上进行即可，不需要到各个点去安装和升级客户端。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;维护方便&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;由于全部的数据和应用程序都放在终端服务器上，云终端上没有任何数据，所以当云终端发生故障时可以很方便地更换，而且云终端出现故障的可能性与普通微机相比要小得多，这样可以大量减少维护工作量，节约人力资源，从而提高劳动效率，以适应越来越激烈的市场竞争。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;减少能耗，美化办公环境&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;云终端能耗通常在5W左右，能为工厂减少能耗，同时大大减轻对UPS的压力。另外云终端体积小，易于放置，能为工厂节约宝贵的办公空间。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21.1pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;虚拟化类型&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; &lt;/b&gt;\r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;服务器虚拟化， 桌面虚拟化，应用程序虚拟化，&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-weight:normal;&quot; class=&quot;15&quot;&gt;存储虚拟化&lt;/span&gt; &lt;/b&gt;\r\n&lt;/p&gt;', './images/article/5756c01a22516.png', '', 1465303066, 2, '当前基于Windows的病毒无孔不入，在大量使用PC的环境中必须确保每台PC都有更好的防护措施，避免因个别PC感染病毒而影响整个网络，这使管理及维护难度大大提高。', 0);
INSERT INTO `dou_article` (`id`, `cat_id`, `title`, `defined`, `content`, `image`, `keywords`, `add_time`, `click`, `description`, `sort`) VALUES
(4, 0, '集中管理', '', '&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;企业ERP及办公OA系统现状分析&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;目前大多企业在ERP及办公OA系统中采用了大量的PC作为工作用机。采用PC的优势在于灵活，本地处理能力非常强大。然而这些优势给我们带来了很大的难题，主要表现为以下几点：&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21.1pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;1、数据安全存在隐患&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;用户可以通过使用移动硬盘等设备任意拷贝数据，造成重要信息外泄，从而给公司带来重大的损失。另外，由于PC本地有硬盘，易受病毒感染，造成重要数据被破坏，也可能给企业带来不可估量的损失。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21.1pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2、维护量大&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;故障率较高。PC配备齐全，经过一段时间的使用后，硬件故障和软件故障逐渐增多，系统维护工作量大，在计算机维护人员相对紧张的情况下，系统管理将随着PC增加而带来更大压力。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;当前基于Windows的病毒无孔不入，在大量使用PC的环境中必须确保每台PC都有更好的防护措施，避免因个别PC感染病毒而影响整个网络，这使管理及维护难度大大提高。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21.1pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;3、系统损坏数据丢失&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;在当前能源短缺严重的情况下，经常出现断电现象，由于在企业中通常没有给PC配备UPS，导致系统易损坏、数据易丢失，从而给我们带来一定的损坏。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21.1pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;4、设备投入量较大&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;PC经常升级换代，基本上PC机使用3年以上就接近报废，无法满足正常的工作需要，需要重新投入购置新机，所以设备投入量较大。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;推荐方案&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:center;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot; align=&quot;center&quot;&gt;\r\n	&lt;span&gt;&lt;img src=&quot;file://C:/Users/ADMINI~1/AppData/Local/Temp/ksohtml/wps8A47.tmp.png&quot; width=&quot;500&quot; height=&quot;339&quot; /&gt;&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;方案说明：&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;1、在本方案中，所有企业员工在工作过程中需要用到的软件（如Office、企业ERP系统等）均统一安装在终端服务器上，只需实现一次安装即可。后续的升级也只需在终端服务器上实现一次升级即可。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;2、企业的信息化系统中，终端主要应用在办公OA、ERP、生产调度等系统中，目前这些系统有基于B/S架构开发的，也有前端基于C/S架构开发的，建议用户采用云终端，使用RDP连接Window2003服务器运行前端基于B/S架构开发的以及前端基于C/S架构开发的系统及OA软件。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;3、在信息系统中，云终端通过RDP与终端服务器的连接时需要的带宽约20K/byte，目前企业内部的网络的带宽基本都已达到100M，均能满足要求，无需进行任何改造。在应用过程中，若客户端数量较多，通常会采用多台服务器来提供终端服务，为了提高终端模式的可靠性和灵活的可扩展性，建议对终端服务器组成网络负载平衡集群。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;4、目前较多的企业在全国各地都有分支机构，这些分支机构与总公司通常没有专线相连，较多的是在Internet上建立VPN连接，对于这样的VPN连接，网络稳定性不能保证，建议在分支机构布置终端服务器，为当地的云终端提供服务。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;应用优势：&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;整体投入低&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;在进行信息系统建议时，云终端方案较PC模式在成本上低10%-20%，但在PC模式中由于其后续的维护成本及升级成本较高，据统计，若使用云终端作为客户端，其5年总TCO比PC模式最多可低57％。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;提高数据了安全性&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;管理员能控制用户的数据输入输出，完全能防止用户使用移动硬盘等设备拷贝信息数据的可能。另外Windows系统最怕非法关机，这样可能损伤硬盘，丢失数据，甚至导致系统崩溃。而采用云终端方案，系统开机后直接进入应用程序，无论何时关机都不会造成系统和数据的损坏，再次开机后仍能回到关机前的状态。完全可以避免由于断电或误操作而引起系统崩溃等一系列问题。同时云终端本地没有硬盘、光驱、软驱等设备，数据没有带出的出口，病毒也没有入口，可以很好的实现数据安全以及有效防范病毒。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;集中管理&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;在PC模式中，客户端的应用软件和IP地址可任意改动，管理极为困难。而采用云终端方案，可以做到真正的集中管理，全部的数据和应用程序都放在终端服务器上，应用程序的安装和版本升级只需要在服务器上进行即可，不需要到各个点去安装和升级客户端。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;维护方便&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;由于全部的数据和应用程序都放在终端服务器上，云终端上没有任何数据，所以当云终端发生故障时可以很方便地更换，而且云终端出现故障的可能性与普通微机相比要小得多，这样可以大量减少维护工作量，节约人力资源，从而提高劳动效率，以适应越来越激烈的市场竞争。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:18pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;减少能耗，美化办公环境&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;云终端能耗通常在5W左右，能为工厂减少能耗，同时大大减轻对UPS的压力。另外云终端体积小，易于放置，能为工厂节约宝贵的办公空间。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;text-indent:21.1pt;background:#ffffff;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;虚拟化类型&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;服务器虚拟化， 桌面虚拟化，应用程序虚拟化，&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-weight:normal;&quot; class=&quot;15&quot;&gt;存储虚拟化&lt;/span&gt; \r\n&lt;/p&gt;', './images/article/5756bfd22dc3b.png', '', 1465303047, 1, '用户可以通过使用移动硬盘等设备任意拷贝数据，造成重要信息外泄，从而给公司带来重大的损失。另外，由于PC本地有硬盘，易受病毒感染，造成重要数据被破坏，也可能给企业带来不可估量的损失。', 0),
(5, 0, '集中管理', '', '&lt;p style=&quot;background:#ffffff;&quot; class=&quot;p&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;适用行业：&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;（一般中小企业；金融业；制造业；流通业；服务业；政府机关；学校教育；&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;电信业；医疗业；）&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;方案概述：&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;今天，对于在商业运作中保持竞争力而言，企业&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;需要依赖于&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;IT &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;，同时&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;着企业网络发展，&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;IT &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;部门需要足够灵活的工具，以便迅速部署良好的&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;IT &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;基础架构以适应和支持企业发展&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;。因此&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;目录服务变得必不可少：&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;目录服务提供一定空间，用于存储与基于网络的实体相关的信息，例&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;如应用程序、文件、打印机和人员。同时，该服务也提供用于命名、描述、定&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;位、存取、管理及保证独立资源信息安全性的一致性方法。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;整合产品：&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;Microsoft Windows Server System &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;相关产品和技术&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;: Windows Server 2003 &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;、&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;Active Directory &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;、&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;DNS &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;、&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;DHCP &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;、组策略管理控制台（&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;GPMC&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;）&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;优势&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;：&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;活动目录通过单一、稳定的管理界面集中管理&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;Windows &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;用户、客户端和服务器，以减少冗&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;、降低维护成本。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;组策略设置定义了系统管理员需要管理的用户桌面环境的各种组件，通过它管理员能够定义&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;并控制对由组织内部的计算机和用户组成的群组进行管理的策略。管理员能够为在活动目录的任一站点、域或组织单元设定组策略。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;通过活动目录和组策略特性，管理员能够针对用户在公司中角色为他们自动分发应用程序。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;活动目录提供强大且一致的安全服务，集中进行管理用户验证和访问控制。向用户提供单一&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;的网络资源登录访问，为管理员提供强大、一致性的工具以使他们能够为内部台式机用户、&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;远程拨号用户以及外部电子商务客户提供的安全服务配置&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;p&quot;&gt;\r\n	&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;p&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;无盘网络解决方案&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;一、前言&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;随着企业多样的化的办公模式，以及企业业务的发展对信息化的需求。将我们信息化建设者推到了企业成长和壮大的前沿。在企业的信息化建设中，复杂多样的网络环境、PC管理不受约束，导致信息化建设者既要考虑如何保障企业日常办公的正常高效，又要考虑如何降低管理维护的难度和时间，还要考虑如何确保个人和企业信息的安全可靠。如何解决这些问题是我们每个信息化建设者的都应有的职业操手。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;二、IT管理员日常遇到的问题&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2.1无法快速统一部署PC机操作系统和应用系统&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;管理员需要手动为每台PC机安装操作系统，更新应用程序或系统设置，工作重复繁杂，消耗大量时间。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2.2 PC机系统故障，难以快速复原解决&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;由于病毒、黑客攻击、员工误操作等因素易导致PC系统故障，查杀病毒、重装系统、配置应用程序，消耗大量时间，影响正常办公。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2.3外设接口管理困难，企业信息易被窃取&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;对软驱、光驱、COM/LPT 端口、USB口、无线网卡、红外、PCMCIA卡等外设的使用行为无法加以控制，导致企业重要信息容易被恶意窃取，无形资产损失严重。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2.4员工进行非办公操作，降低工作效率&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;某些员工玩游戏、安装QQ、MSN来聊天、访问非法网站，既增加系统中毒的可能性，又降低工作效率。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2.5内部网络与Internet的相对分离难以控制&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;在企业内部，有些岗位的PC设备需要连接Internet，但外网的病毒可能会通过PC设备带入内部网络，以致蔓延整个内部办公网络。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2.6企业数据的安全控制&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;由于员工的不规范的操作导致的误删或者蓄意的删除企业的数据，有可能引起无法挽回的损失。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;综合以上的几个问题，推出企业办公解决方案&lt;/span&gt; \r\n&lt;/p&gt;', './images/article/5756bfc2082f7.jpg', '', 1465302978, 2, '', 0);
INSERT INTO `dou_article` (`id`, `cat_id`, `title`, `defined`, `content`, `image`, `keywords`, `add_time`, `click`, `description`, `sort`) VALUES
(6, 0, '设接口管理困难，企业信息易被窃取', '', '&lt;p style=&quot;background:#ffffff;&quot; class=&quot;p&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;适用行业：&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;（一般中小企业；金融业；制造业；流通业；服务业；政府机关；学校教育；&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;电信业；医疗业；）&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;方案概述：&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;今天，对于在商业运作中保持竞争力而言，企业&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;需要依赖于&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;IT &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;，同时&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;着企业网络发展，&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;IT &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;部门需要足够灵活的工具，以便迅速部署良好的&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;IT &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;基础架构以适应和支持企业发展&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;。因此&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;目录服务变得必不可少：&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;目录服务提供一定空间，用于存储与基于网络的实体相关的信息，例&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;如应用程序、文件、打印机和人员。同时，该服务也提供用于命名、描述、定&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;位、存取、管理及保证独立资源信息安全性的一致性方法。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;整合产品：&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;Microsoft Windows Server System &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;相关产品和技术&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;: Windows Server 2003 &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;、&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;Active Directory &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;、&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;DNS &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;、&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;DHCP &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;、组策略管理控制台（&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;GPMC&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;）&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;优势&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot; class=&quot;15&quot;&gt;：&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;活动目录通过单一、稳定的管理界面集中管理&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;Windows &lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;用户、客户端和服务器，以减少冗&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;、降低维护成本。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;组策略设置定义了系统管理员需要管理的用户桌面环境的各种组件，通过它管理员能够定义&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;并控制对由组织内部的计算机和用户组成的群组进行管理的策略。管理员能够为在活动目录的任一站点、域或组织单元设定组策略。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;通过活动目录和组策略特性，管理员能够针对用户在公司中角色为他们自动分发应用程序。&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;活动目录提供强大且一致的安全服务，集中进行管理用户验证和访问控制。向用户提供单一&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;的网络资源登录访问，为管理员提供强大、一致性的工具以使他们能够为内部台式机用户、&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;远程拨号用户以及外部电子商务客户提供的安全服务配置&lt;/span&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;p&quot;&gt;\r\n	&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;p&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;无盘网络解决方案&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;一、前言&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;随着企业多样的化的办公模式，以及企业业务的发展对信息化的需求。将我们信息化建设者推到了企业成长和壮大的前沿。在企业的信息化建设中，复杂多样的网络环境、PC管理不受约束，导致信息化建设者既要考虑如何保障企业日常办公的正常高效，又要考虑如何降低管理维护的难度和时间，还要考虑如何确保个人和企业信息的安全可靠。如何解决这些问题是我们每个信息化建设者的都应有的职业操手。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;二、IT管理员日常遇到的问题&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2.1无法快速统一部署PC机操作系统和应用系统&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;管理员需要手动为每台PC机安装操作系统，更新应用程序或系统设置，工作重复繁杂，消耗大量时间。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2.2 PC机系统故障，难以快速复原解决&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;由于病毒、黑客攻击、员工误操作等因素易导致PC系统故障，查杀病毒、重装系统、配置应用程序，消耗大量时间，影响正常办公。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:left;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2.3外设接口管理困难，企业信息易被窃取&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;对软驱、光驱、COM/LPT 端口、USB口、无线网卡、红外、PCMCIA卡等外设的使用行为无法加以控制，导致企业重要信息容易被恶意窃取，无形资产损失严重。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2.4员工进行非办公操作，降低工作效率&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;某些员工玩游戏、安装QQ、MSN来聊天、访问非法网站，既增加系统中毒的可能性，又降低工作效率。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2.5内部网络与Internet的相对分离难以控制&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;在企业内部，有些岗位的PC设备需要连接Internet，但外网的病毒可能会通过PC设备带入内部网络，以致蔓延整个内部办公网络。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;2.6企业数据的安全控制&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;font-weight:bold;&quot;&gt;&lt;/span&gt;&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:21pt;&quot; class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;由于员工的不规范的操作导致的误删或者蓄意的删除企业的数据，有可能引起无法挽回的损失。&lt;/span&gt;&lt;span style=&quot;line-height:150%;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;font-family:宋体;color:#000000;font-size:10.5pt;&quot;&gt;综合以上的几个问题，推出企业办公解决方案&lt;/span&gt; \r\n&lt;/p&gt;', './images/article/5756bfb4630f3.jpg', '', 1465302964, 0, '', 0);

