INSERT INTO `dou_product` (`id`, `cat_id`, `name`, `price`, `defined`, `content`, `image`, `keywords`, `add_time`, `description`, `sort`) VALUES
(1, 1, '华为（HUAWEI）AR1220-S 8口百兆企业级VPN路由器', '5400.00', '', '优肯全万兆交换机UK6410-08HC采用Broadcom芯片，是一款标准的三层无阻塞交换机。提供178Gbps交换容量，132.43Mpps转发性能，L2/L3全线速转发；具有灵活的组网能力，保证网络的稳定运行和链路的负载均衡。采用模块化设计，提供8个10GE SFP+端口，8个10/100/1000M RJ45电口，1个Console口；给汇聚和核心的数据交换提供足够的带宽，用户可以按需求选择合适的万兆口以灵活的应对网络需求。\r\n&lt;div&gt;\r\n	&lt;span style=&quot;color:#e53333;&quot;&gt;&lt;strong&gt;产品特点：&lt;/strong&gt;&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;深圳时速科技旗下品牌优肯推出的全万兆交换机UK6410-08HC采用Broadcom芯片，是一款标准的三层无阻塞交换机。提供&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;178&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;Gbps交换容量，&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;132.43&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;Mpps转发性能，L2/L3全线速转发；具有灵活的组网能力，保证网络的稳定运行和链路的负载均衡。采用模块化设计，提供&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;8&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;个10GE&amp;nbsp;SFP+端口，8个10/100/1000M&amp;nbsp;RJ45电口，1个Console口；给汇聚和核心的数据交换提供足够的带宽，用户可以按需求选择合适的万兆口以灵活的应对网络需求。同时具有高带宽、低时延、网络管理简易等特性，定位于下一代数据中心及云计算网络中的高密接入，也可用于大中型小网吧、企业网、城域网的核心或汇聚。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;1、性能卓越&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;具有&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;178&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;Gbps背板带宽，业界最高性能（1U&amp;nbsp;TOR）；&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;硬件路由，线速三层交换包转发率达到&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;132.43&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;Mpps,L2/L3全线速转发；&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;可部署在城域网核心，并提供几个最后1千米技术&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;2、安全可靠&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;提供DOS、分布式拒绝服务、欺骗以及病毒等防御&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;支持IEEE802.1x，提供基于端口的用户认证、网盾技术、安全控制技术以及策略控制列表&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;限制端口最大连接主机数量，以太网接口进行多路绑定，增加带宽和系统冗余&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;强大的ACL，支持L2-L4层数据过滤&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;策略控制列表、流量调节&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;3、使用维护方便&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;支持灵活的端口配置&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;可以通过Web、SNMP、CLI、telnet等多种方式管理&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;支持XModem协议，可以通过TFTP/ZMODEM进行软件升级和BootRom升级&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;支持SFP+，同时也向下兼容XFP等&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;在PHY进入或退出低功耗状态。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;4、强大的流量和广播管理功能&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;自动检测并抑制广播风暴，支持IGMP报文的侦测，有效限制广播报文的泛滥&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;支持全双工模式和半双工模式的流量控制&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;支持以太网接口以1M为步长的速率限制&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n		&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;支持IP组播和QoS&lt;/span&gt; \r\n	&lt;/p&gt;\r\n&lt;!--EndFragment--&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;span style=&quot;color:#e53333;&quot;&gt;&lt;strong&gt;技术参数：&lt;/strong&gt;&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;table style=&quot;border-collapse:collapse;&quot; align=&quot;center&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td style=&quot;background:#ffffff;&quot; valign=&quot;center&quot; width=&quot;126&quot;&gt;\r\n				&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n					&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;产品型号&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td style=&quot;background:#ffffff;&quot; valign=&quot;center&quot; width=&quot;546&quot; colspan=&quot;2&quot;&gt;\r\n				&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n					&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;UK6410-&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;08&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;HC&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td style=&quot;background:#ffffff;&quot; valign=&quot;center&quot; width=&quot;126&quot;&gt;\r\n				&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n					&lt;span style=&quot;font-family:微软雅黑;&qu