	&lt;td style=&quot;background:#ffffff;&quot; valign=&quot;center&quot; width=&quot;126&quot;&gt;\r\n				&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n					&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;背板带宽&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td style=&quot;background:#ffffff;&quot; valign=&quot;top&quot; width=&quot;546&quot; colspan=&quot;2&quot;&gt;\r\n				&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n					&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;178&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;Gbps&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td style=&quot;background:#ffffff;&quot; valign=&quot;center&quot; width=&quot;126&quot;&gt;\r\n				&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n					&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;转发速率&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td style=&quot;background:#ffffff;&quot; valign=&quot;top&quot; width=&quot;546&quot; colspan=&quot;2&quot;&gt;\r\n				&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n					&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;132.43&lt;/span&gt;&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;Mpps&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td style=&quot;background:#ffffff;&quot; valign=&quot;center&quot; width=&quot;126&quot;&gt;\r\n				&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n					&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;巨型帧&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td style=&quot;background:#ffffff;&quot; valign=&quot;top&quot; width=&quot;546&quot; colspan=&quot;2&quot;&gt;\r\n				&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n					&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;12k&amp;nbsp;bytes&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td style=&quot;background:#ffffff;&quot; valign=&quot;center&quot; width=&quot;12