示&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td style=&quot;background:#ffffff;&quot; valign=&quot;center&quot; width=&quot;126&quot;&gt;\r\n				&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n					&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;环境温/湿度&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n			&lt;td style=&quot;background:#ffffff;&quot; valign=&quot;top&quot; width=&quot;544&quot;&gt;\r\n				&lt;p style=&quot;text-align:justify;&quot;&gt;\r\n					&lt;span style=&quot;font-family:微软雅黑;&quot;&gt;工作温度：0°C～50°C（0m～1800m）&amp;nbsp;存储温度：-40°C～+70°C，&amp;nbsp;相对湿度：5%RH～95%RH，非凝露&lt;/span&gt; \r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;', './images/product/5756b7033f80b.jpg', '', 1465300739, '', 0);

